mapper.select:
SELECT plan.asset_id As assetId, br.borrow_nid AS borrowNid, bpt.borrow_name AS projectTypeName, b.borrow_Period AS borrowPeriod, b.borrow_Apr AS borrowApr, br.recover_fee AS recoverFee, br.nid AS accedeOrderId, b.account AS borrowAccount, b.borrow_account_yes AS borrowAccountYes, b.manage_fee_rate As manageFeeRate, bs.name AS repayType, ui.truename AS recoverTrueName, u2.username AS recoverUserName, t.account AS recoverTotal, br.recover_capital AS recoverCapital, br.recover_interest AS recoverInterest, '' As serviceRate, '' As serviceFee, '' As recoverSumTotal FROM ht_borrow AS b INNER JOIN ht_borrow_recover AS br ON br.borrow_nid=b.borrow_nid LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid LEFT join ht_borrow_tender AS t on br.tender_id=t.id LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = CAST(b.project_type AS CHAR) LEFT JOIN ht_users AS u1 ON br.borrow_userid = u1.user_id LEFT JOIN ht_users AS u2 ON br.user_id = u2.user_id LEFT JOIN ht_users_info ui ON u2.user_id = ui.user_id LEFT JOIN ht_spreads_users su ON su.user_id = u2.user_id LEFT JOIN ht_users spu ON spu.user_id = su.spreads_userid LEFT JOIN ht_users_info ui2 ON ui2.user_id = su.spreads_userid LEFT JOIN `ht_crm`.`oa_users` `ou` ON `ou`.hyd_id = br.user_id AND ou.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN `ht_crm`.`oa_department` `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN `ht_crm`.`oa_department` `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN `ht_crm`.`oa_department` `od3` ON `od3`.`id` = `od2`.`parentid` LEFT JOIN `ht_crm`.`oa_users` `ou1` ON `ou1`.hyd_id = su.spreads_userid AND ou1.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN `ht_crm`.`oa_department` `od4` ON `od4`.`id` = `ou1`.`departmentid` AND `od4`.id IS NOT NULL LEFT JOIN `ht_crm`.`oa_department` `od5` ON `od5`.`id` = `od4`.`parentid` LEFT JOIN `ht_crm`.`oa_department` `od6` ON `od6`.`id` = `od5`.`parentid` LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = b.inst_code LEFT JOIN ht_hjh_plan_asset plan ON plan.borrow_nid = b.borrow_nid
ORDER BY br.recover_time desc
LIMIT #{limitStart} , #{limitEnd}
