mapper.select:
SELECT ac.account AS account,bt.nid AS nid,bt.borrow_nid AS borrowNid ,ifnull(re.recover_capital,'') AS recoverCapital ,ifnull(FROM_UNIXTIME( re.recover_time, '%Y-%m-%d %H:%i:%s' ),'') AS recoverTime ,ifnull(re.recover_interest,'') AS recoverInterest,ifnull(recover_account,'') AS recoverAccount, case b.status when '4' then '1' when '5' then '2' else '0' end as status, bpt.borrow_class as borrow_type, iii.repay_interest AS repay_interest, iii.repay_interest_yes AS repay_interest_yes, case iii.repay_action_time when '0' then '' else ifnull(FROM_UNIXTIME( iii.repay_action_time, '%Y-%m-%d %H:%i:%s' ),'') end AS repay_action_time FROM huiyingdai_borrow_tender bt INNER JOIN huiyingdai_borrow b ON bt.borrow_nid = b.borrow_nid LEFT JOIN hyjf_increase_interest_invest iii ON iii.tender_nid = bt.nid LEFT JOIN huiyingdai_borrow_style bs ON bs.nid = b.borrow_style LEFT JOIN hyjf_param_name pnai ON CAST(bt.invest_type AS CHAR) = pnai.name_cd AND pnai.name_class = 'INVEST_TYPE' LEFT JOIN hyjf_hjh_inst_config hic ON hic.inst_code = b.inst_code LEFT JOIN huiyingdai_borrow_project_type bpt ON bpt.borrow_cd = CAST(b.project_type AS CHAR) LEFT JOIN huiyingdai_borrow_recover re ON bt.id = re.tender_id LEFT JOIN hyjf_bank_open_account ac ON bt.user_id = ac.user_id WHERE 1=1
AND b.inst_code = #{instCode}
AND bt.addtime >= unix_timestamp(#{startTime})
AND bt.addtime <= unix_timestamp(#{endTime})+86399
AND ac.account = #{account}
AND bt.borrow_nid = #{borrowNid}
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
