sql.where:
AND hbr.borrow_nid = #{borrowNid}
AND hbr.nid = #{tenderNid}
AND hbr.recover_status = 0 AND hb.project_type != 8 AND hb.project_type != 13 AND hb.repay_last_time >= (#{nowTime} + 24 * 3600 * 30) AND hb.borrow_style != 'month' AND hb.borrow_style != 'principal' AND hb.plan_nid IS NULL AND hb.inst_code = 10000000 AND hbr.advance_status != 3 AND hbr.delay_days = 0 AND hbr.credit_time <= (#{nowTime} - 3600 * 24 * 3) AND hbr.recover_capital >= 1000 AND (hbr.recover_capital-credit_amount) >= 1000 AND hbr.create_time <= (#{nowTime} - 3600 * 24 * 30) AND hbr.recover_time >= (#{nowTime} + 24 * 3600 * 3) AND hbr.addtime <= (#{nowTime} - 24 * 3600 * 3) AND CAST(IFNULL(hbr.repay_orddate,0) AS SIGNED) < FROM_UNIXTIME((#{nowTime} - 24 * 3600 * 3),'%Y%m%d') AND hbr.user_id = #{userId}
AND ( CASE WHEN hb.borrow_style = 'endmonth' THEN ( SELECT hbrp.recover_status AS recoverPlanStatus FROM ht_borrow_recover_plan hbrp WHERE hbrp.borrow_nid = hbr.borrow_nid AND hbrp.tender_id = hbr.tender_id AND hbrp.recover_period = ( hb.borrow_period - hbr.recover_period + 1 ) ) ELSE (hbr.recover_status) END )<>1

mapper.select:
SELECT hb.borrow_nid AS borrow_nid, CONCAT(hb.borrow_apr,'%') AS borrow_apr, CONCAT(hb.borrow_period,'个月') AS borrow_period, hbr.recover_time AS recover_time, FORMAT(( IFNULL(hbr.recover_capital, 0) - IFNULL(hbr.recover_capital_yes, 0) - IFNULL(hbr.credit_amount, 0) ),2) AS credit_account, CONCAT('剩余',(DATEDIFF( FROM_UNIXTIME( CAST(hb.repay_last_time AS SIGNED), '%Y-%m-%d 00:00:00' ), FROM_UNIXTIME(#{nowTime}, '%Y-%m-%d 00:00:00') )),'天') AS last_days, CONCAT(#{host},'?borrowNid=',hb.borrow_nid,'&tenderNid=',ht.nid,'&sign=',#{sign},'&platform=',#{platform}) AS tender_credit_url FROM ht_borrow_recover hbr LEFT JOIN ht_borrow_tender ht ON ht.borrow_nid = hbr.borrow_nid AND ht.nid = hbr.nid LEFT JOIN ht_borrow hb ON hbr.borrow_nid = hb.borrow_nid
ORDER BY ht.addtime ASC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT CONCAT(substring(hydu.username,1,1),'**') AS user_name, SUBSTRING(FORMAT(hydct.assign_capital,4),1,LENGTH(FORMAT(hydct.assign_capital,4))-2) AS account, from_unixtime( hydct.create_time, '%Y-%m-%d %H:%i:%s' ) AS invest_time FROM ht_credit_tender hydct LEFT JOIN ht_r_user hydu ON hydct.user_id = hydu.user_id
hydct.credit_nid = #{creditNid,jdbcType=VARCHAR}
ORDER BY hydct.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
