mapper.sql:
where hba.api_type = #{apiType}

    AND hba.borrow_nid = #{borrowNid}



    AND b.inst_code = #{instCodeSrch}


    AND hba.batch_no = #{batchNo}


    AND cast(hba.`status` AS CHAR) = #{status}



    AND FROM_UNIXTIME(hba.create_time, '%Y-%m-%d %H:%i:%S') >= STR_TO_DATE(CONCAT(#{submitTimeStartSrch},'00:00:00'
    ),'%Y-%m-%d %H:%i:%s')



    AND FROM_UNIXTIME(hba.create_time, '%Y-%m-%d %H:%i:%S') <= STR_TO_DATE(CONCAT(#{submitTimeEndSrch},'23:59:59'
    ),'%Y-%m-%d %H:%i:%s')

mapper.select:
SELECT hba.id, hba.borrow_nid borrow_nid, hic.inst_name, hba.batch_no batch_no, hba.user_id user_id, hba.user_name user_name, hba.period_now period_now, hba.borrow_period borrow_period, hba.borrow_account borrow_account, hba.batch_amount batch_amount, hba.batch_service_fee batch_service_fee, hba.batch_counts batch_counts, hba.suc_amount suc_amount, hba.suc_counts suc_counts, hba.fail_amount fail_amount, hba.fail_counts fail_counts, hba.`status`, from_unixtime( hba.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, from_unixtime( hba.update_time, '%Y-%m-%d %H:%i:%s' ) AS update_time, hba.is_repay_org_flag is_repay_org_flag, hba.data FROM ht_borrow_apicron hba LEFT JOIN ht_borrow b ON hba.borrow_nid = b.borrow_nid LEFT JOIN ht_borrow_info bi ON hba.borrow_nid = bi.borrow_nid LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = bi.inst_code
ORDER BY hba.create_time DESC
LIMIT #{limitStart} , #{limitEnd}
