mapper.select:
SELECT SUM(a) FROM (SELECT COUNT(*) a FROM ht_borrow_tender WHERE
user_id = #{userId}
UNION ALL
SELECT COUNT(*) a FROM ht_credit_tender WHERE user_id = #{userId}
UNION ALL
SELECT COUNT(*) a FROM ht_borrow_tender_cpn WHERE user_id = #{userId}
UNION ALL
SELECT COUNT(*) a FROM ht_hjh_accede WHERE user_id = #{userId})b

SELECT hydb.borrow_nid as borrowNid, hydbpt.borrow_project_type AS projectType , hydbpt.borrow_cd AS TYPE, hydbpt.borrow_name AS typeName, hbi.`name` AS borrowName, IFNULL(hbi.project_name,hydb.borrow_nid) AS projectName, hbi.borrow_contents as borrowContents, hbi.borrow_measures_mea as borrowMeasuresMea, hbi.is_new as isNew, hbi.finance_purpose as financePurpose, hbi.monthly_income monthlyIncome, hbi.payment, hbi.first_payment firstPayment, hbi.second_payment secondPayment, hbi.cost_introdution costIntrodution, hbi.fiance_condition fianceCondition, hydb.borrow_style borrowStyle, hbi.inst_code instCode, hbi.asset_type assetType, hydb.is_show as isShow, CASE WHEN hbi.company_or_personal = 2 THEN ( SELECT m.credit FROM ht_borrow_maninfo m WHERE m.borrow_nid = hydb.borrow_nid ) ELSE ( SELECT u.credit FROM ht_borrow_user u WHERE u.borrow_nid = hydb.borrow_nid ) END AS userCredit, hbi.borrow_measures_instit AS measuresInstit, hydb.account AS borrowAccount, TRUNCATE(hydb.account/10000,2) AS borrowAccountWan, hydb.borrow_apr as borrowApr, hydb.borrow_period as borrowPeriod, CASE WHEN hydb.borrow_style = 'endday' THEN '天' ELSE '个月' END AS borrowPeriodType, hydb.borrow_account_wait AS investAccount, hbi.company_or_personal AS comOrPer, hydbs.`name` AS repayStyle,
TRUNCATE(hbi.tender_account_min/10000,2) tenderAccountMinWan, hbi.tender_account_max tenderAccountMax, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN FROM_UNIXTIME(hydb.ontime, '%Y-%m-%d %H:%i') ELSE FROM_UNIXTIME( hydb.verify_time, '%Y-%m-%d %H:%i' ) END AS sendTime, FROM_UNIXTIME( hydb.borrow_full_time, '%Y-%m-%d %H:%i:%s' ) AS fullTime, FROM_UNIXTIME( (hydb.verify_time + hydb.borrow_valid_time * 24 * 60 * 60), '%Y-%m-%d %H:%i:%s' ) AS borrowEndTime, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN FROM_UNIXTIME( hydb.ontime, '%Y-%m-%d %H:%i:%s') ELSE '' END AS onTime, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN hydb.ontime ELSE '' END AS TIME, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN '10' WHEN hydb.`status` = 2 AND hydb.borrow_full_status = 0 AND ( hydb.verify_time + ( hydb.borrow_valid_time * 24 * 60 * 60 ) > UNIX_TIMESTAMP(NOW()) ) THEN '11' WHEN hydb.`status` = 3 THEN '12' WHEN hydb.`status` = 4 THEN '13' WHEN hydb.`status` = 5 THEN '14'
WHEN hydb.`status` = 8 THEN '16' ELSE '' END AS `status`, hydb.`status` AS statusOrginal, hbi.borrow_increase_money increaseMoney, hbi.borrow_interest_coupon interestCoupon, hbi.borrow_taste_money tasteMoney, hbi.borrow_asset_number borrowAssetNumber, hbi.borrow_project_source borrowProjectSource, hbi.borrow_interest_time borrowInterestTime, hbi.borrow_due_time borrowDueTime, hbi.borrow_safeguard_way borrowSafeguardWay, hbi.borrow_income_description borrowIncomeDescription, hbi.borrow_publisher borrowPublisher, hbi.borrow_extra_yield borrowExtraYield, hbi.contract_period contractPeriod, FROM_UNIXTIME( hydb.recover_last_time, '%Y年%m月%d日' ) recoverLastTime, hbi.borrow_level borrowLevel, hydb.invest_level investLevel FROM ht_borrow hydb LEFT JOIN ht_borrow_project_type hydbpt ON hydbpt.borrow_cd = hydb.project_type left join ht_borrow_info hbi on hydb.borrow_nid = hbi.borrow_nid LEFT JOIN ht_borrow_style hydbs ON hydbs.nid = hydb.borrow_style WHERE ( hydbpt.borrow_project_type = 'HZT' OR hydbpt.borrow_project_type = 'HXF' ) AND ( ( ( hydb.`status` = 1 AND hydb.verify_status = 3 ) ) OR ( hydb.`status` = 2 AND hydb.borrow_full_status = 0 AND ( hydb.verify_time + ( hydb.borrow_valid_time * 24 * 60 * 60 ) > UNIX_TIMESTAMP(NOW()) ) ) OR ( hydb.`status` = 3 ) OR ( hydb.`status` = 4 ) OR ( hydb.`status` = 5 ) OR ( hydb.`status` = 8 ) ) and hydb.borrow_nid =#{borrowNid,jdbcType=VARCHAR}
