mapper.select:
SELECT bc.credit_id, bc.credit_nid, bc.credit_user_id, CASE WHEN bc.client = 1 THEN '微信' WHEN bc.client = 2 THEN 'android' WHEN bc.client = 3 THEN 'ios' WHEN bc.client = 0 THEN 'pc' ELSE '其他' END as client, bc.bid_nid, u.username, FORMAT(bc.credit_capital, 2) AS credit_capital, FORMAT(bc.credit_capital, 2) AS credit_capital_price, CONCAT(credit_discount, '%') AS credit_discount, FORMAT(bc.credit_price, 2) AS credit_price, bc.credit_capital_assigned, CASE WHEN bc.credit_status = '1' THEN '停止' WHEN bc.credit_status = '2' THEN '完全承接' ELSE '进行中' END AS credit_status_name, CASE WHEN bc.repay_status= 0 THEN '未还款' WHEN bc.repay_status = 1 THEN '还款成功' WHEN bc.repay_status = 2 THEN '还款失败' ELSE '还款失败' END AS repay_status_name, CASE WHEN bc.credit_status = '1' THEN '1' WHEN bc.credit_status = '2' THEN '2' ELSE CASE WHEN bc.end_time < UNIX_TIMESTAMP(NOW()) THEN '1' ELSE '0' END END AS credit_status, bc.create_time AS add_time, CASE WHEN b.repay_last_time != 0 AND b.repay_last_time IS NOT NULL THEN FROM_UNIXTIME( b.repay_last_time, '%Y-%m-%d %H:%i:%s' ) ELSE '' END AS repay_last_time FROM ht_borrow_credit bc INNER JOIN ht_borrow b ON bc.bid_nid = b.borrow_nid LEFT JOIN ht_r_user u ON u.user_id = bc.credit_user_id
ORDER BY bc.create_time DESC
LIMIT #{limitStart} , #{limitEnd}

SELECT CONCAT(substring(hydu.username, 1, 1),'**') AS userName, 0 as vipId, 0 as vipLevel, SUBSTRING(FORMAT(hhdct.assign_capital,4),1,LENGTH(FORMAT(hhdct.assign_capital,4))-2) AS account, hhdct.client AS client, date_format( hhdct.create_time,'%Y-%c-%d %H:%i:%s') AS undertakeTime FROM ht_hjh_debt_credit_tender hhdct LEFT JOIN ht_r_user hydu ON hhdct.user_id = hydu.user_id
hhdct.borrow_nid = #{borrowNid,jdbcType=VARCHAR}
ORDER BY hhdct.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
