mapper.select:
SELECT cu.id, cc.coupon_code, cu.coupon_user_code, cc.coupon_name, CASE cu.attribute WHEN 0 THEN '无主单' WHEN 1 THEN '有主单' WHEN 2 THEN '线下员工' WHEN 3 THEN '线上员工' ELSE '' END AS attribute, cu.channel, CASE cc.coupon_type WHEN 1 THEN '体验金' WHEN 2 THEN '加息券' WHEN 3 THEN '代金券' END AS coupon_type, CASE cc.coupon_type WHEN 1 THEN CONCAT( FORMAT(cc.coupon_quota, 2), '元' ) WHEN 2 THEN CONCAT(cc.coupon_quota, '%') WHEN 3 THEN CONCAT( FORMAT(cc.coupon_quota, 2), '元' ) ELSE CONCAT(cc.coupon_quota, '%') END AS coupon_quota, CASE WHEN cu.coupon_source = 1 THEN '手动发放' WHEN cu.coupon_source = 2 THEN '活动发放' WHEN cu.coupon_source = 3 THEN '会员礼包' ELSE '' END AS coupon_source,
CASE WHEN cu.coupon_source = 1 THEN cu.content WHEN cu.coupon_source = 2 THEN al.title ELSE '' END AS coupon_content, FROM_UNIXTIME( cu.end_time, '%Y-%m-%d' ) AS end_time, cu.used_flag, CASE WHEN cu.used_flag = 0 THEN '未使用' WHEN cu.used_flag = 1 THEN '已使用' WHEN cu.used_flag = 2 THEN '审核不通过' WHEN cu.used_flag = 3 THEN '待审核' WHEN cu.used_flag = 4 THEN '已失效' END used_flag_View, FROM_UNIXTIME( cu.add_time, '%Y-%m-%d %H:%i:%S' ) AS add_time, cc.coupon_system AS coupon_system, cc.project_type AS project_type, CASE WHEN cc.tender_quota_type = 0 THEN '投资金额不限' WHEN cc.tender_quota_type = 1 THEN CONCAT( cc.tender_quota_min, '元~', cc.tender_quota_max, '元可用' ) WHEN cc.tender_quota_type = 2 THEN CONCAT( '满', cc.tender_quota, '元可用' ) WHEN cc.tender_quota_type = 3 THEN CONCAT( cc.tender_quota, '元（含）内可用' ) ELSE '投资金额不限' END tender_quota, CASE WHEN cc.project_expiration_type = 0 THEN '项目期限不限' WHEN cc.project_expiration_type = 1 THEN CONCAT( '等于', cc.project_expiration_length, '月' ) WHEN cc.project_expiration_type = 2 THEN CONCAT( cc.project_expiration_length_min, '月~', cc.project_expiration_length_max, '月' ) WHEN cc.project_expiration_type = 3 THEN CONCAT( '大于等于', cc.project_expiration_length, '月' ) WHEN cc.project_expiration_type = 4 THEN CONCAT( '小于等于', cc.project_expiration_length, '月' ) ELSE '项目期限不限' END project_expiration_type FROM ht_coupon_user cu JOIN ht_coupon_config cc ON cu.coupon_code = cc.coupon_code AND cc.del_flg = 0 LEFT JOIN ht_activity_list al ON cu.activity_id = al.id WHERE cu.user_id = #{userId} ORDER BY cu.id DESC
LIMIT #{limitStart} , #{limitEnd}
