sql.where:
    cu.del_flag = 0

		AND cu.user_id = #{userId}


		AND cu.coupon_code = #{couponCode}


		AND cu.coupon_user_code = #{couponUserCode}


		AND u.username = #{username}


		AND cc.coupon_type = #{couponType}


		AND cu.used_flag = #{usedFlag}


		AND cu.coupon_source = #{couponSource}


		AND cu.end_time >= #{timeStartSrch}


		AND cu.end_time <= #{timeEndSrch}


		AND cu.create_time >= #{timeStartAddSrch}


		AND cu.create_time <= #{timeEndAddSrch}

mapper.select:
SELECT cu.id,cc.coupon_name,CASE cc.coupon_type WHEN 1 THEN '体验金' WHEN 2 THEN '加息券' WHEN 3 THEN '代金券' ELSE '' END AS coupon_type, cc.coupon_type AS coupon_type_origin, CASE cc.coupon_type WHEN 2 THEN CONCAT(cc.coupon_quota,'%') ELSE FORMAT(cc.coupon_quota, 0) END AS coupon_quota,cc.coupon_quota AS coupon_quota_origin, CONCAT(FROM_UNIXTIME(cu.create_time, '%Y.%m.%d'), '-', FROM_UNIXTIME(cu.end_time, '%Y.%m.%d'))AS end_time, cc.project_type as project_type,cc.coupon_system as coupon_system, cc.tender_quota as tender_quota_origin,cc.project_expiration_length as project_expiration_length,cc.tender_quota_min as tender_quota_min,cc.tender_quota_max as tender_quota_max, cc.project_expiration_type as project_expiration_type,cc.project_expiration_length_min as project_expiration_length_min, cc.project_expiration_length_max as project_expiration_length_max,cc.tender_quota_type as tender_quota_type, CONCAT(FROM_UNIXTIME(cu.create_time, '%Y.%m.%d'), '-', FROM_UNIXTIME(cu.end_time, '%Y.%m.%d'))AS time, CASE WHEN cc.tender_quota_type = 0 THEN '金额不限' WHEN cc.tender_quota_type = 1 THEN CONCAT( cc.tender_quota_min, '元~', cc.tender_quota_max, '元可用' ) WHEN cc.tender_quota_type = 2 THEN CONCAT( '满', cc.tender_quota, '元可用' ) WHEN cc.tender_quota_type = 3 THEN CONCAT( cc.tender_quota, '元（含）内可用' ) ELSE '不限' END tender_quota, cc.tender_quota as tender_quota_value, CONCAT(#{host},REPLACE('&id=',' ', ''),cu.id) AS detail_url, #{usedFlag} AS coupon_status FROM ht_coupon_user cu JOIN ht_coupon_config cc ON cu.coupon_code = cc.coupon_code LEFT JOIN ht_r_user u ON cu.user_id = u.user_id
ORDER BY cu.end_time ,cu.create_time, cu.coupon_code
LIMIT #{limitStart} , #{limitEnd}
