mapper.update:
update ht_hjh_plan
set
available_invest_account = IFNULL(available_invest_account,0) - #{availableInvestAccount,jdbcType=DECIMAL}
where
plan_nid = #{planNid,jdbcType=VARCHAR}

update ht_hjh_plan
set

	join_total = IFNULL(join_total,0) + #{joinTotal,jdbcType=DECIMAL},


	available_invest_account = IFNULL(available_invest_account,0) + #{availableInvestAccount,jdbcType=DECIMAL}

where
plan_nid = #{planNid,jdbcType=VARCHAR}

mapper.select:
SELECT COUNT(1) FROM ( SELECT DISTINCT * FROM( (SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, IFNULL(hbs.NAME, '') NAME, hb.create_time
FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style left join ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid JOIN ht_hjh_debt_credit ON credit_status IN (0, 1) AND hb.borrow_nid = ht_hjh_debt_credit.borrow_nid WHERE ht_hjh_debt_credit.plan_nid_new = #{planNid} AND hb.STATUS = 4 ORDER BY hb.create_time ASC ) UNION ALL ( SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, IFNULL(hbs.NAME, '') NAME, hb.create_time FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style left join ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid WHERE hb.plan_nid = #{planNid} AND hb.STATUS = 2 AND hb.borrow_full_status = 0 ORDER BY hb.create_time ASC ) UNION ALL ( SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, IFNULL(hbs.NAME, '') NAME, hb.create_time FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style left join ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid WHERE hb.plan_nid = #{planNid} AND hb.borrow_full_status = 1
AND hb.borrow_full_time >= #{startTime}
AND hb.borrow_full_time <= #{endTime}
ORDER BY hb.create_time ASC ) UNION ALL ( SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, IFNULL(hbs.NAME, '') NAME, hb.create_time FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style left join ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid JOIN ht_hjh_debt_credit ON credit_capital_wait = 0
AND end_time >= #{startTime}
AND end_time <= #{endTime}
AND hb.borrow_nid = ht_hjh_debt_credit.borrow_nid WHERE ht_hjh_debt_credit.plan_nid_new = #{planNid} ORDER BY hb.create_time ASC ))t ) AS t

SELECT DISTINCT * FROM( (SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period_str, IFNULL(hbs.NAME, '') NAME, hb.create_time
FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style JOIN ht_hjh_debt_credit ON credit_status IN (0, 1) AND hb.borrow_nid = ht_hjh_debt_credit.borrow_nid LEFT JOIN ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid
WHERE ht_hjh_debt_credit.plan_nid_new = #{planNid} AND hb.STATUS = 4 ORDER BY hb.create_time ASC ) UNION ALL ( SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period_str, IFNULL(hbs.NAME, '') NAME, hb.create_time FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style LEFT JOIN ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid WHERE hb.plan_nid = #{planNid} AND hb.STATUS = 2 AND hb.borrow_full_status = 0 ORDER BY hb.create_time ASC ) UNION ALL ( SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period_str, IFNULL(hbs.NAME, '') NAME, hb.create_time FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style LEFT JOIN ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid
WHERE hb.plan_nid = #{planNid} AND hb.borrow_full_status = 1
AND hb.borrow_full_time >= #{startTime}
AND hb.borrow_full_time <= #{endTime}
ORDER BY hb.create_time ASC ) UNION ALL ( SELECT hb.borrow_nid, hb.borrow_user_name, hb.borrow_apr, hb.account, hbi.company_or_personal, IFNULL( hbi.finance_purpose, '无借款用途' ) financePurpose, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period_str, IFNULL(hbs.NAME, '') NAME, hb.create_time FROM ht_borrow hb LEFT JOIN ht_borrow_style hbs ON hbs.nid = hb.borrow_style LEFT JOIN ht_borrow_info hbi on hbi.borrow_nid = hb.borrow_nid JOIN ht_hjh_debt_credit ON credit_capital_wait = 0
AND end_time >= #{startTime}
AND end_time <= #{endTime}
AND hb.borrow_nid = ht_hjh_debt_credit.borrow_nid WHERE ht_hjh_debt_credit.plan_nid_new = #{planNid} ORDER BY hb.create_time ASC ))t
LIMIT #{limitStart} , #{limitEnd}

SELECT dpa.user_id as userId, CASE WHEN LENGTH(dpa.user_name)>1 THEN CONCAT(SUBSTR(dpa.user_name,1,1),'**') ELSE dpa.user_name END userName, SUBSTRING(FORMAT(dpa.accede_account,4),1,LENGTH(FORMAT(dpa.accede_account,4))-2) AS accedeAccount, date_format(dpa.create_time, '%Y-%m-%d %H:%i:%s') as accedeTime, -- IFNULL(hui.vip_id,0) vip_id, -- hui.vip_value, dpa.client FROM ht_hjh_accede dpa LEFT JOIN ht_r_user hui ON hui.user_id = dpa.user_id -- LEFT JOIN ht_param_name hpn ON dpa.client=hpn.name_cd AND hpn.name_class='CLIENT' WHERE dpa.plan_nid=#{planNid} -- add by nxl 加入计划最后三天的记录
AND dpa.create_time between #{startTime} and #{endTime}
ORDER BY dpa.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT dpa.user_id, CASE WHEN LENGTH(dpa.user_name)>1 THEN CONCAT(SUBSTR(dpa.user_name,1,1),'**') ELSE dpa.user_name END user_name, SUBSTRING(FORMAT(dpa.accede_account,4),1,LENGTH(FORMAT(dpa.accede_account,4))-2) AS accede_account, DATE_FORMAT( dpa.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, dpa.client FROM ht_hjh_accede dpa WHERE dpa.plan_nid=#{planNid} -- add by nxl 加入计划最后三天的记录
AND dpa.create_time between #{startTime} and #{endTime}
ORDER BY dpa.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT count(dpa.accede_order_id)
FROM
ht_hjh_accede dpa
WHERE dpa.plan_nid=#{planNid}
-- add by nxl 加入计划最后三天的记录

	AND dpa.create_time between #{startTime} and #{endTime}
