SELECT SUM(t.account) summoney, SUM(t.yearAccount) yearmoney, SUM(t.commission) AS commission FROM ( SELECT t1.account account, CASE WHEN t4.borrow_style = 'endday' THEN ROUND( ( t1.account * t4.borrow_period / 365 ), 2 ) ELSE ROUND( ( t1.account * t4.borrow_period / 12 ), 2 ) END yearAccount, CASE WHEN t4.borrow_style = 'endday' THEN ROUND( ( t1.account * t4.borrow_period / 365 )*0.07, 2 ) ELSE ROUND( ( t1.account * t4.borrow_period / 12 )*0.07, 2 ) END commission FROM ht_borrow_tender t1 LEFT JOIN ht_r_user t2 ON t1.user_id = t2.user_id LEFT JOIN ht_borrow t4 ON t1.borrow_nid = t4.borrow_nid LEFT JOIN ht_borrow_info i on i.borrow_nid=t4.borrow_nid LEFT JOIN ht_r_user t5 ON t5.user_id = t2.spreads_user_id AND t1.accede_order_id IS NULL where t1.user_id in
#{userId} AND t1.accede_order_id IS NULL
and t2.create_time >= #{regTimeStart} and t2.create_time <= #{regTimeEnd}
AND t1.create_time >= #{addTimeStart} AND t1.create_time <= #{addTimeEnd}
) t

SELECT SUM(t.account) summoney, SUM(t.yearAccount) yearmoney, SUM(t.commission) AS commission FROM ( SELECT t1.accede_account account, CASE WHEN t4.is_month = 1 THEN ROUND( ( t1.accede_account * t4.lock_period / 12 ) * 0.07, 2 ) ELSE ROUND( ( t1.accede_account * t4.lock_period / 365 ) * 0.07, 2 ) END commission,
CASE WHEN t4.is_month = 1 THEN ROUND( ( t1.accede_account * t4.lock_period / 12 ), 2 ) ELSE ROUND( ( t1.accede_account * t4.lock_period / 365 ) , 2 ) END yearAccount FROM ht_hjh_accede t1 LEFT JOIN ht_r_user t2 ON t1.user_id = t2.user_id LEFT JOIN ht_hjh_plan t4 ON t1.plan_nid = t4.plan_nid LEFT JOIN ht_r_user t5 ON t5.user_id = t2.spreads_user_id where t1.user_id in
#{userId}
and t2.create_time>= #{regTimeStart} and t2.create_time <= #{regTimeEnd}
AND t1.create_time >= #{addTimeStart} AND t1.create_time <= #{addTimeEnd}
) t

SELECT nid AS nid,create_time AS ADDTIME,user_id AS user_id FROM ht_borrow_tender where 1=1
AND user_id= #{userId}
UNION ALL SELECT credit_tender_nid AS nid,create_time AS ADDTIME,user_id AS user_id FROM ht_credit_tender where 1=1
AND user_id= #{userId}
UNION ALL SELECT nid AS nid,create_time AS ADDTIME,user_id AS user_id FROM ht_borrow_tender_cpn where 1=1
AND user_id= #{userId}
UNION ALL SELECT accede_order_id AS nid,create_time AS ADDTIME,user_id AS user_id FROM ht_hjh_accede where 1=1
AND user_id= #{userId}
order by ADDTIME limit 1
