sql.where:
AND hbr.borrow_nid = #{borrowNid}
AND hbr.nid = #{tenderNid}
AND hbr.recover_status = 0 AND hb.project_type != 8 AND hb.project_type != 13 AND hb.plan_nid IS NULL AND bi.inst_code = 10000000 AND hb.repay_last_time >= (#{nowTime} + 24 * 3600 * 30) AND hb.borrow_style != 'month' AND hb.borrow_style != 'principal'
AND hbr.delay_days = 0 AND hbr.credit_time <= (#{nowTime} - 3600 * 24 * 3) AND hbr.recover_capital >= 1000 AND (hbr.recover_capital-credit_amount) >= 1000 AND hbr.create_time <= (#{nowTime} - 3600 * 24 * 30) AND hbr.recover_time >= (#{nowTime} + 24 * 3600 * 3) AND hbr.create_time <= (#{nowTime} - 24 * 3600 * 3) AND CAST(IFNULL(hbr.repay_orddate,0) AS SIGNED) < FROM_UNIXTIME((#{nowTime} - 24 * 3600 * 3),'%Y%m%d') AND hbr.user_id = #{userId}
AND ( CASE WHEN hb.borrow_style = 'endmonth' THEN ( SELECT hbrp.recover_status AS recoverPlanStatus FROM ht_borrow_recover_plan hbrp WHERE hbrp.borrow_nid = hbr.borrow_nid AND hbrp.tender_id = hbr.tender_id AND hbrp.recover_period = ( hb.borrow_period - hbr.recover_period + 1 ) ) ELSE (hbr.recover_status) END )<>1

mapper.select:
SELECT * FROM (SELECT hb.name AS borrow_name, hb.borrow_nid AS borrow_nid, hb.project_type AS borrow_type, hb.borrow_apr AS borrow_apr, hb.account AS borrow_account, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, hb.borrow_style AS borrow_style, CASE WHEN hb.borrow_style = 'endday' OR hb.borrow_style = 'end' THEN 1 WHEN hb.borrow_style = 'endmonth' THEN hb.borrow_period - hbr.recover_period END AS period_now, bs.`name` AS borrow_style_name, hbr.recover_time AS recover_time, (DATEDIFF( FROM_UNIXTIME( CAST(#{nowTime} AS SIGNED), '%Y-%m-%d 00:00:00' ), FROM_UNIXTIME(hb.recover_last_time, '%Y-%m-%d 00:00:00') )) AS tender_period, FROM_UNIXTIME( IFNULL(ht.addtime,0), '%Y-%m-%d %H:%i:%s' ) AS tender_time, hbr.nid AS tender_nid, hbr.recover_capital AS invest_account, ( IFNULL(hbr.recover_capital, 0) - IFNULL(hbr.recover_capital_yes, 0) - IFNULL(hbr.credit_amount, 0) ) AS credit_account, FROM_UNIXTIME( CAST(hb.repay_last_time AS SIGNED), '%Y-%m-%d' ) AS repay_last_time, (DATEDIFF( FROM_UNIXTIME( CAST(hb.repay_last_time AS SIGNED), '%Y-%m-%d 00:00:00' ), FROM_UNIXTIME(#{nowTime}, '%Y-%m-%d 00:00:00') )) AS last_days FROM ht_borrow_recover hbr LEFT JOIN ht_borrow_tender ht ON ht.borrow_nid = hbr.borrow_nid AND ht.nid = hbr.nid LEFT JOIN ht_borrow hb ON hbr.borrow_nid = hb.borrow_nid LEFT JOIN ht_borrow_style bs ON bs.nid = hb.borrow_style
) t1
ORDER BY tender_time ${tenderTimeSort}
ORDER BY credit_account ${creditAccountSort}
ORDER BY tender_period ${tenderPeriodSort}
ORDER BY last_days ${remainDaysSort}
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

select IFNULL(SUM(IFNULL(credit_capital,0)),0) AS credit_capital from ht_borrow_credit INNER JOIN ht_borrow ON ht_borrow_credit.bid_nid = ht_borrow.borrow_nid INNER JOIN ht_borrow_style hbs ON ht_borrow.borrow_style = hbs.nid LEFT JOIN ht_borrow_tender hbt ON ht_borrow_credit.bid_nid = hbt.borrow_nid AND ht_borrow_credit.tender_nid = hbt.nid
credit_status = 0
AND credit_user_id = #{userId}

select IFNULL(SUM(IFNULL(credit_capital_assigned,0)),0) AS credit_capital from ht_borrow_credit INNER JOIN ht_borrow ON ht_borrow_credit.bid_nid = ht_borrow.borrow_nid INNER JOIN ht_borrow_style hbs ON ht_borrow.borrow_style = hbs.nid LEFT JOIN ht_borrow_tender hbt ON ht_borrow_credit.bid_nid = hbt.borrow_nid AND ht_borrow_credit.tender_nid = hbt.nid
1=1
AND credit_user_id = #{userId}

SELECT
	COUNT(hbc.credit_id)
FROM
	ht_borrow_credit hbc


		AND hbc.credit_user_id = #{userId}


		AND hbc.create_date = #{nowDate}

SELECT
COUNT(hct.assign_id) AS total
FROM
ht_credit_tender hct
LEFT JOIN ht_borrow_credit hbc ON hct.credit_nid=hbc.credit_nid AND hct.credit_tender_nid=hbc.tender_nid


		AND hct.user_id = #{userId}


		AND hbc.credit_user_id = #{creditUserId}


		AND hbc.credit_nid = #{creditNid}


		AND hbc.credit_status = #{creditStatus}

SELECT hct.assign_id AS assignId, hct.user_id AS userId, hct.credit_user_id AS creditUserId, hct.`status` AS `status`, hct.bid_nid AS bidNid, hct.credit_nid AS creditNid, hct.credit_tender_nid AS creditTenderNid, hct.assign_nid AS assignNid, hct.assign_capital AS assignCapital, hct.assign_account AS assignAccount, hct.assign_interest AS assignInterest, hct.assign_interest_advance AS assignInterestAdvance, hct.assign_price AS assignPrice, hct.assign_pay AS assignPay, hct.assign_repay_account AS assignRepayAccount, hct.assign_repay_capital AS assignRepayCapital, hct.assign_repay_interest AS assignRepayInterest, CASE WHEN hct.assign_repay_end_time != 0 THEN FROM_UNIXTIME( hct.assign_repay_end_time, '%Y-%m-%d' ) ELSE NULL END AS assignRepayEndTime, CASE WHEN hct.assign_repay_last_time != 0 THEN FROM_UNIXTIME( hct.assign_repay_last_time, '%Y-%m-%d' ) ELSE NULL END AS assignRepayLastTime, CASE WHEN hct.assign_repay_next_time != 0 THEN FROM_UNIXTIME( hct.assign_repay_next_time, '%Y-%m-%d' ) ELSE NULL END AS assignRepayNextTime, CASE WHEN hct.assign_repay_yes_time != 0 THEN FROM_UNIXTIME( hct.assign_repay_yes_time, '%Y-%m-%d' ) ELSE NULL END AS assignRepayYesTime, hct.assign_repay_period AS assignRepayPeriod, DATE_FORMAT(hct.assign_create_date,'%Y-%m-%d') AS assignCreateDate, hct.credit_fee AS creditFee, DATE_FORMAT(hct.create_time,'%Y-%m-%d %H:%i:%s') AS createTime, DATE_FORMAT(hct.create_time,'%Y-%m-%d') AS createTimeDay, DATE_FORMAT(hct.create_time,'%H:%i:%s') AS createTimeHour, hbc.credit_term AS creditTerm, hbc.bid_apr AS bidApr, hbc.credit_discount AS creditDiscount, DATE_FORMAT( hbt.create_time, '%Y-%m-%d' ) AS tenderTime, hui.username AS creditAssignUserTrueName FROM ht_credit_tender hct LEFT JOIN ht_borrow_credit hbc ON hct.credit_nid=hbc.credit_nid AND hct.credit_tender_nid=hbc.tender_nid LEFT JOIN ht_borrow_tender hbt ON hbc.tender_nid=hbt.nid AND hbc.bid_nid=hbt.borrow_nid LEFT JOIN ht_r_user hui ON hui.user_id = hct.user_id
AND hct.user_id = #{userId}
AND hbc.credit_user_id = #{creditUserId}
AND hbc.credit_nid = #{creditNid}
AND hbc.credit_status = #{creditStatus}
ORDER BY hct.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT IFNULL(SUM(IFNULL(hct.assign_capital,0)),0) AS assignCapital, IFNULL(SUM(IFNULL(hct.assign_interest_advance,0)),0) AS assignInterestAdvance, IFNULL(SUM(IFNULL(hct.assign_pay,0) - IFNULL(hct.credit_fee,0)),0) AS moneyGet, IFNULL(SUM(IFNULL(hct.credit_fee,0)),0) AS creditFee, hbc.credit_capital AS creditCapital, hbc.bid_nid AS bidNid, DATE_FORMAT(hbc.create_time,'%Y-%m-%d %H:%i') AS createTime FROM ht_credit_tender hct LEFT JOIN ht_borrow_credit hbc ON hct.credit_nid=hbc.credit_nid AND hct.credit_tender_nid=hbc.tender_nid LEFT JOIN ht_borrow_tender hbt ON hbc.tender_nid=hbt.nid AND hbc.bid_nid=hbt.borrow_nid
AND hct.user_id = #{userId}
AND hbc.credit_user_id = #{creditUserId}
AND hbc.credit_nid = #{creditNid}
AND hbc.credit_status = #{creditStatus}
