sql.where:
hydal.user_id = #{userId,jdbcType=INTEGER} AND hydal.is_show = 0
AND CAST(DATE_FORMAT(hydal.create_time,'%Y') AS SIGNED) = #{tradeYear,jdbcType=INTEGER}
AND CAST(DATE_FORMAT(hydal.create_time,'%m') AS SIGNED) = #{tradeMonth,jdbcType=INTEGER}
AND unix_timestamp(hydal.create_time) >= unix_timestamp(CONCAT(#{startDate,jdbcType=VARCHAR},' 00:00:00'))
AND unix_timestamp(hydal.create_time) <= unix_timestamp(CONCAT(#{endDate,jdbcType=VARCHAR},' 23:59:59'))
AND hydal.trade = #{tradeType,jdbcType=VARCHAR}
AND hydal.trade IN ( 'increase_interest_profit', 'experience_profit', 'cash_coupon_profit' )
AND hydal.trade IN ( 'recharge', 'borrow_success', 'tender_recover_yes', 'cash_tiaozhang', 'borrow_spreads_tender', 'borrowactivity', 'redeem_huitianli', 'interest_huitianli', 'creditsell', 'recharge_success', 'credit_tender_recover_yes', 'increase_interest_profit', 'experience_profit', 'cash_coupon_profit', 'plan_spreads_accede', 'plan_repay', 'platform_transfer', 'increase_interest_repay_yes', 'directional_transfer_receive', 'recharge_offline', 'tender', 'repay_freeze', 'unFrz', 'tender_success', 'cash_success', 'tender_huitianli', 'account_change', 'repay_success', 'creditassign', 'user_transfer', 'apply_vip', 'directional_transfer_pay', 'accede_plan', 'hjh_quit', 'hjh_invest' )

mapper.select:
SELECT hydal.id, #{roleId,jdbcType=VARCHAR} AS roleId, #{trade,jdbcType=VARCHAR} AS trade, DATE_FORMAT(hydal.create_time,'%Y-%m-%d %H:%i:%s') AS time, CASE WHEN hydal.`type` = 1 THEN '+' WHEN hydal.`type` = 2 THEN '-' ELSE '' END AS type, hydat.`name` AS type_name, hydat.`value` AS trade, IFNULL(hydal.amount,0) AS money, CASE WHEN hydal.is_bank = 1 THEN IFNULL(hydal.bank_balance, 0) ELSE IFNULL(hydal.balance, 0) END AS balance, IFNULL(hydal.remark, '') AS borrowNid, hydal.is_bank AS is_bank, hydat.type_id AS `revuAndExpType`, hydal.trade_status AS trade_status, hydal.remark FROM ht_account_list hydal LEFT JOIN ht_account_trade hydat ON hydal.trade = hydat.`value` AND hydat.`status` = 1
ORDER BY hydal.create_time DESC,hydal.id DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT hydar.id, DATE_FORMAT( hydar.create_time, '%Y-%m-%d %H:%i:%s' ) AS time, hydar.money AS money, hydar.balance AS balance, hydar.fee AS fee, hydar.is_bank AS is_bank, hydar.`status` AS `status` FROM ht_account_recharge hydar
ORDER BY hydar.create_time DESC,hydar.id DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT hydaw.id, DATE_FORMAT( hydaw.create_time, '%Y-%m-%d %H:%i:%s' ) time, hydaw.total AS money, hydaw.fee AS fee, hydaw.credited AS balance, hydaw.`status` AS `status`, hydaw.bank_flag AS bank_flag FROM ht_account_withdraw hydaw
ORDER BY hydaw.create_time DESC,hydaw.id DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT DATE_FORMAT( hydal.create_time, '%Y-%m-%d %H:%i:%s' ) tradeTime, SUBSTRING(FORMAT(IF(hydal.is_bank=0,hydal.balance,hydal.bank_balance),4),1,LENGTH(FORMAT(IF(hydal.is_bank=0,hydal.balance,hydal.bank_balance),4))-2) bankBalance, date_format(hydal.create_time, '%Y' ) AS tradeYear, date_format(hydal.create_time, '%m' ) AS tradeMonth, hydat.`name` AS tradeType, CASE WHEN hydal.trade IN ( 'recharge', 'borrow_success', 'tender_recover_yes', 'cash_tiaozhang', 'borrow_spreads_tender', 'borrowactivity', 'redeem_huitianli', 'interest_huitianli', 'creditsell', 'recharge_success', 'credit_tender_recover_yes', 'increase_interest_profit', 'experience_profit', 'cash_coupon_profit', 'plan_spreads_accede', 'plan_repay', 'platform_transfer', 'increase_interest_repay_yes', 'directional_transfer_receive', 'recharge_offline', 'hjh_quit' ) THEN CONCAT('+', SUBSTRING(FORMAT(hydal.amount,4),1,LENGTH(FORMAT(hydal.amount,4))-2)) WHEN hydal.trade IN ( 'tender_success', 'cash_success', 'tender_huitianli', 'account_change', 'repay_success', 'creditassign', 'user_transfer', 'apply_vip', 'directional_transfer_pay', 'accede_plan', 'hjh_invest' ) THEN CONCAT('-', SUBSTRING(FORMAT(hydal.amount,4),1,LENGTH(FORMAT(hydal.amount,4))-2)) ELSE SUBSTRING(FORMAT(hydal.amount,4),1,LENGTH(FORMAT(hydal.amount,4))-2) END AS account, hydal.is_bank FROM ht_account_list hydal LEFT JOIN ht_account_trade hydat ON hydal.trade = hydat.`value` AND hydat.`status` = 1
ORDER BY hydal.create_time DESC,hydal.id DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
