sql.where:
ht_borrow.is_show <> 1

    AND credit_status = #{creditStatus}


    AND borrow_period >= #{borrowPeriodMin}

        AND borrow_period < #{borrowPeriodMax}


AND ((
    ht_borrow.borrow_style !='endday'

        AND ht_borrow_credit.bid_apr >=  #{borrowAprMin}

            AND ht_borrow_credit.bid_apr < #{borrowAprMax}


) OR (
    ht_borrow.borrow_style ='endday'

        AND ht_borrow_credit.bid_apr >=  #{borrowAprMin} * 30

            AND ht_borrow_credit.bid_apr < #{borrowAprMax} * 30


))

mapper.select:
select t.* from ( (SELECT 'HJH' borrow_type, t.plan_nid borrow_nid, t.plan_name borrow_name, t.expect_apr borrow_apr, t.borrow_period borrow_period, t.borrow_period_type borrow_period_type, t.order_by_status_name status, t.plan_invest_status_name on_time, t.order_by_available_invest_account account_wait, '1' type , 0 borrow_account_scale, '0' verify_time, '' borrow_extra_yield FROM (SELECT hp.id as order_by_id, hp.plan_nid AS plan_nid, hp.plan_name AS plan_name, hp.expect_apr AS expect_apr, CASE WHEN hp.borrow_style = 'endday' THEN CONCAT(hp.lock_period, '天') ELSE CONCAT(hp.lock_period, '个月') END AS lock_period, hp.lock_period borrow_period, CASE WHEN hp.borrow_style = 'endday' THEN '天' ELSE '个月' END borrow_period_type, CASE WHEN hp.plan_invest_status = 1 THEN CONCAT( FORMAT( hp.available_invest_account / 10000, 2 ), '万' ) WHEN hp.plan_invest_status = 2 THEN '0.00 万' ELSE '0.00 万' END AS available_invest_account, CASE WHEN hp.plan_invest_status = 1 THEN hp.available_invest_account ELSE 0 END AS order_by_available_invest_account, hp.plan_invest_status AS plan_invest_status, CASE WHEN hp.plan_invest_status = 1 AND FORMAT(hp.available_invest_account, 2) <= 0.00 THEN '稍后开启' WHEN hp.plan_invest_status = 1 THEN '立即加入' WHEN hp.plan_invest_status = 2 THEN '稍后开启' ELSE '' END AS plan_invest_status_name, CASE WHEN hp.plan_invest_status = 1 AND FORMAT(hp.available_invest_account, 2) <= 0.00 THEN 2 WHEN hp.plan_invest_status = 1 THEN 1 WHEN hp.plan_invest_status = 2 THEN 2 ELSE 2 END AS order_by_status_name, CASE WHEN hp.borrow_style = 'endday' THEN hp.lock_period ELSE hp.lock_period * 30 END AS order_by_lock_period, coupon_config FROM ht_hjh_plan hp WHERE hp.del_flag = 0 AND hp.plan_display_status = 1 ) t where t.order_by_status_name=1
AND 1=2
) UNION ALL (SELECT hydbpt.borrow_class AS borrow_type, hydb.borrow_nid,
hydb.borrow_nid AS borrow_name,
hydb.borrow_apr, hydb.borrow_period borrow_period, CASE WHEN hydb.borrow_style = 'endday' THEN '天' ELSE '个月' END borrow_period_type, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN '10' WHEN hydb.`status` = 2 AND ( hydb.verify_time + ( hydb.borrow_valid_time * 24 * 60 *60 ) > UNIX_TIMESTAMP( NOW() ) ) THEN '11' WHEN hydb.`status` = 3 THEN '12' WHEN hydb.`status` = 4 THEN '13' ELSE '' END AS tstatus, CASE WHEN hydb.`status` = 1 AND hydb.verify_status =3 THEN FROM_UNIXTIME( hydb.ontime, '%m-%d %H:%i' ) ELSE '' END AS on_time, IFNULL(hydb.borrow_account_wait,0) borrow_account_wait, '2' type, hydb.borrow_account_scale borrow_account_scale, hydb.verify_time verify_time, case when borrowInfo.borrow_extra_yield=0 then '' else CONCAT(borrowInfo.borrow_extra_yield, '%') end borrow_extra_yield FROM ht_borrow hydb INNER JOIN ht_borrow_info borrowInfo ON borrowInfo.borrow_nid = hydb.borrow_nid LEFT JOIN ht_borrow_project_type hydbpt ON hydbpt.borrow_cd = hydb.project_type WHERE hydbpt.borrow_project_type = 'HZT' AND hydb.project_type <> '4' AND hydb.project_type <> '11' AND hydb.is_show <> 1 AND ( hydb.`status` = 2 AND hydb.verify_time + ( hydb.borrow_valid_time * 24 * 60 *60 ) > UNIX_TIMESTAMP( NOW() ) ) ) UNION ALL (SELECT hydbpt.borrow_class AS borrow_type, hydb.borrow_nid,
hydb.borrow_nid AS borrow_name,
hydb.borrow_apr, hydb.borrow_period borrow_period, CASE WHEN hydb.borrow_style = 'endday' THEN '天' ELSE '个月' END borrow_period_type, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN '10' WHEN hydb.`status` = 2 AND ( hydb.verify_time + ( hydb.borrow_valid_time * 24 * 60 *60 ) > UNIX_TIMESTAMP( NOW() ) ) THEN '11' WHEN hydb.`status` = 3 THEN '12' WHEN hydb.`status` = 4 THEN '13' ELSE '' END AS tstatus, CASE WHEN hydb.`status` = 1 AND hydb.verify_status =3 THEN FROM_UNIXTIME( hydb.ontime, '%m-%d %H:%i' ) ELSE '' END AS on_time, IFNULL(hydb.borrow_account_wait,0) borrow_account_wait, '2' type, hydb.borrow_account_scale borrow_account_scale, hydb.verify_time verify_time, case when borrowInfo.borrow_extra_yield=0 then '' else CONCAT(borrowInfo.borrow_extra_yield, '%') end borrow_extra_yield FROM ht_borrow hydb INNER JOIN ht_borrow_info borrowInfo ON borrowInfo.borrow_nid = hydb.borrow_nid LEFT JOIN ht_borrow_project_type hydbpt ON hydbpt.borrow_cd = hydb.project_type WHERE hydbpt.borrow_project_type = 'HZT' AND hydb.project_type <> '4' AND hydb.project_type <> '11' AND hydb.is_show <> 1 AND ( hydb.`status` = 1 AND hydb.verify_status = 3 )
) ) t order by t.type,t.`status` ASC,t.on_time ASC, t.borrow_account_scale DESC, t.verify_time DESC,t.status ASC,t.account_wait ASC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
