mapper.select:
SELECT a.user_id, a.bank_flag, a.account_id, a.seq_no, DATE_FORMAT(a.tx_date,'%Y-%m-%d') as tx_date, CONCAT(MID(lpad(a.tx_time,6,0),1,2),':',MID(lpad(a.tx_time,6,0),3,2),':',MID(lpad(a.tx_time,6,0),5,2)) as tx_time, a.bank_seq_no, a.create_time, a.id, u.username, u.attribute AS user_property, u.mobile, CASE u.role_id WHEN 1 THEN '投资人' WHEN 2 THEN '借款人' END AS roleid , u.attribute AS user_attribute, CASE WHEN u.attribute = '1' THEN `od6`.`name` ELSE `od3`.`name` END `user_region_name`, CASE WHEN u.attribute = '1' THEN `od5`.`name` ELSE `od2`.`name` END `user_branch_name`, CASE WHEN u.attribute = '1' THEN `od4`.`name` ELSE `od`.`name` END `user_department_name`, spu.username AS referrer_name, u.spreads_user_id AS referrer_user_id, spu.truename AS referrer_true_name, `od6`.`name` AS `referrer_region_name`, `od5`.`name` AS `referrer_branch_name`, `od4`.`name` AS `referrer_department_name`, a.nid AS ordid, a.total, a.fee, a.credited, a.bank AS bank, a.account, FROM_UNIXTIME( a.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time_str, a.client AS client_str, a.`status` AS `status`, a.`status` AS status_str, a.reason AS reason, a.withdraw_type AS withdrawType FROM ht_account_withdraw a LEFT JOIN ht_r_user u ON a.user_id = u.user_id LEFT JOIN ht_r_user spu ON spu.user_id = u.spreads_user_id LEFT JOIN ht_r_oa_users `ou` ON `ou`.hyd_id = u.user_id AND ou.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN ht_r_oa_department `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN ht_r_oa_department `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN ht_r_oa_department `od3` ON `od3`.`id` = `od2`.`parentid` LEFT JOIN ht_r_oa_users `ou1` ON `ou1`.hyd_id = u.spreads_user_id AND ou1.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN ht_r_oa_department `od4` ON `od4`.`id` = `ou1`.`departmentid` AND `od4`.id IS NOT NULL LEFT JOIN ht_r_oa_department `od5` ON `od5`.`id` = `od4`.`parentid` LEFT JOIN ht_r_oa_department `od6` ON `od6`.`id` = `od5`.`parentid`
ORDER BY a.create_time DESC
limit #{limitStart} , #{limitEnd}

SELECT hu.username username, hu.truename truename, haw.total total, haw.credited credited, haw.fee fee, IFNULL(hbc.card_no,'银行卡已删除') card_no, haw.bank bank, haw.`status` status, FROM_UNIXTIME( haw.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, haw.bank_flag FROM ht_account_withdraw haw LEFT JOIN ht_r_user hu ON haw.user_id = hu.user_id LEFT JOIN ht_bank_card hbc ON haw.bank_id=hbc.id WHERE haw.bank_flag=1
AND haw.user_id = #{userId}
AND haw.status = #{status}
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
