SELECT IFNULL(sum(temp.c),0) tenderCount, IFNULL(sum(temp.s),0) tenderMoney FROM ( SELECT count(1) as c, IFNULL(SUM(t.account),0) as s FROM ht_borrow_tender t WHERE t.create_time >= #{startTime} AND t.create_time < #{endTime} AND t.user_id in
#{item}
UNION
SELECT count(1) as c, IFNULL(SUM(t.assign_pay),0) as s FROM ht_credit_tender t WHERE t.create_time >= #{startTime} AND t.create_time < #{endTime} AND t.user_id in
#{item}
UNION SELECT count(1) as c, IFNULL(SUM(t.accede_account),0) as s FROM ht_hjh_accede t WHERE t.create_time >= #{startTime} AND t.create_time < #{endTime} AND t.user_id in
#{item}
) temp

SELECT IFNULL(sum(temp.c),0) tenderFirstCount, IFNULL(sum(temp.s),0) tenderFirstMoney FROM ( SELECT count(1) as c, IFNULL(SUM(t.account),0) as s FROM ht_borrow_tender t WHERE t.create_time >= #{startTime} AND t.create_time < #{endTime} AND t.user_id in
#{item}
AND NOT EXISTS ( SELECT t1.user_id FROM ht_borrow_tender t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time ) AND NOT EXISTS ( SELECT t1.user_id FROM ht_credit_tender t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time ) AND NOT EXISTS ( SELECT t1.user_id FROM ht_hjh_accede t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time )
UNION
SELECT count(1) as c, IFNULL(SUM(t.assign_pay),0) as s FROM ht_credit_tender t WHERE t.create_time >= #{startTime} AND t.create_time < #{endTime} AND t.user_id in
#{item}
AND NOT EXISTS ( SELECT t1.user_id FROM ht_borrow_tender t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time ) AND NOT EXISTS ( SELECT t1.user_id FROM ht_credit_tender t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time ) AND NOT EXISTS ( SELECT t1.user_id FROM ht_hjh_accede t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time )
UNION SELECT count(1) as c, IFNULL(SUM(t.accede_account),0) as s FROM ht_hjh_accede t WHERE t.create_time >= #{startTime} AND t.create_time < #{endTime} AND t.user_id in
#{item}
AND NOT EXISTS ( SELECT t1.user_id FROM ht_borrow_tender t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time ) AND NOT EXISTS ( SELECT t1.user_id FROM ht_credit_tender t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time ) AND NOT EXISTS ( SELECT t1.user_id FROM ht_hjh_accede t1 WHERE t1.user_id = t.user_id AND t1.create_time < t.create_time )
) temp
