mapper.select:
SELECT info.name AS borrow_name, bt.user_name AS username, bt.invite_user_name AS referrer_name, bt.borrow_nid, bt.user_id AS userid, bt.nid AS nid, CONCAT(b.borrow_apr, '%') borrow_apr, CASE WHEN b.borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, CASE WHEN b.`status` = 4 THEN 1 WHEN b.`status` = 5 THEN 1 ELSE 0 END resendMessage, bs.`name` AS borrow_style_name, FORMAT(bt.account, 2) AS account, bt.client AS operating_deck, bt.tender_user_attribute AS user_property, bt.invest_type AS investType, iii.borrow_extra_yield AS borrowExtraYield, FROM_UNIXTIME( bt.add_time, '%Y-%m-%d %H:%i:%s' ) AS addtime FROM ht_borrow_tender bt INNER JOIN ht_borrow b ON bt.borrow_nid = b.borrow_nid INNER JOIN ht_borrow_info info ON bt.borrow_nid = info.borrow_nid LEFT JOIN ht_borrow_style bs ON bs.nid = b.borrow_style LEFT JOIN ht_increase_interest_invest iii ON iii.tender_id = bt.id
ORDER BY bt.add_time DESC, bt.id DESC
LIMIT #{limitStart} , #{limitSize}

SELECT hdpa.invite_user_name referee_name, hdpa.plan_nid, CONCAT(hdp.expect_apr,'%') expect_apr, CONCAT(hdp.debt_lock_period,'个月') debt_lock_period, hdpa.accede_account, '到期一次性还本付息' repayment_method, CASE hdpa.user_attribute WHEN 0 THEN '无主单' WHEN 1 THEN '有主单' WHEN 2 THEN '线下员工' WHEN 3 THEN '线上员工' ELSE '' END AS user_attribute, hdpa.client client, CASE WHEN hcc.coupon_type = '1' THEN '体验金' WHEN hcc.coupon_type = '2' THEN '加息券' WHEN hcc.coupon_type = '3' THEN '代金券' ELSE '现金' END AS tender_type, FROM_UNIXTIME(hdpa.create_time,'%Y-%m-%d %H:%i') AS create_time, hdpa.create_time AS add_time_long FROM ht_debt_plan_accede hdpa LEFT JOIN ht_debt_plan hdp ON hdpa.plan_nid = hdp.debt_plan_nid LEFT JOIN ht_coupon_real_tender hcrt ON hcrt.real_tender_id = hdpa.accede_order_id LEFT JOIN ht_coupon_tender hct ON hct.order_id = hcrt.coupon_tender_id LEFT JOIN ht_coupon_user hcu ON hcu.id = hct.coupon_grant_id LEFT JOIN ht_coupon_config hcc ON hcc.coupon_code = hcu.coupon_code
ORDER BY hdpa.create_time DESC
limit #{limitStart} , #{limitSize}
