mapper.select:
select hu.username AS userName, huc.username AS creditUserName, hct.status AS status, hct.bid_nid AS bidNid, hct.credit_nid AS creditNid, hct.credit_tender_nid AS creditTenderNid, hct.assign_nid AS assignNid, hct.assign_capital AS assignCapital, hct.assign_account AS assignAccount, hct.assign_interest AS assignInterest, hct.assign_price AS assignPrice, hct.assign_pay AS assignPay, hct.assign_repay_account AS assignRepayAccount, (select sum(manage_fee) from ht_credit_repay where assign_nid= hct.assign_nid) AS creditFee, CASE WHEN hct.assign_repay_end_time != 0 THEN FROM_UNIXTIME(hct.assign_repay_end_time, '%Y-%m-%d') ELSE '' END AS assignRepayEndTime, CASE WHEN hct.assign_repay_next_time != 0 THEN FROM_UNIXTIME(hct.assign_repay_next_time, '%Y-%m-%d') ELSE '' END AS assignRepayNextTime, hct.create_time AS addTime FROM ht_credit_tender hct INNER JOIN ht_r_user hu ON hct.user_id = hu.user_id INNER JOIN ht_r_user huc ON hct.credit_user_id = huc.user_id
ORDER BY hct.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
