mapper.select:
SELECT count(*) FROM (
	SELECT user_id FROM ht_borrow_tender where client=#{type}
	UNION ALL
	SELECT user_id FROM ht_product_list where client=#{type}
	UNION ALL
	SELECT user_id FROM ht_debt_plan_accede where client=#{type}
	UNION ALL
	SELECT user_id FROM ht_credit_tender) a

		a.client=#{type}

			and a.user_id in

				#{item}



			AND a.create_time >= #{dayStart}


			AND a.create_time <= #{dayEnd}

select sum(hbt.account) from ht_borrow_tender hbt left join ht_borrow hb on hbt.borrow_nid = hb.user_id

	hb.project_type <> 8
	AND hb.project_type <> 13

		and hbt.user_id in

			#{item}



		AND hbt.create_time >= #{dayStart}


		AND hbt.create_time <= #{dayEnd}

select sum(hbt.account) from ht_borrow_tender hbt left join ht_borrow hb on hbt.borrow_nid = hb.user_id

	hb.project_type = 8

		and hbt.user_id in

			#{item}



		AND hbt.create_time >= #{dayStart}


		AND hbt.create_time <= #{dayEnd}

select sum(hbt.account) from ht_borrow_tender hbt left join ht_borrow hb on hbt.borrow_nid = hb.user_id

		hb.project_type = 13

			and hbt.user_id in

				#{item}



			AND hbt.create_time >= #{dayStart}


			AND hbt.create_time <= #{dayEnd}
