sql.where:
AND hydal.user_id = #{userId,jdbcType=INTEGER}
AND hydal.is_show = 0

	AND hydat.`value`= #{trade,jdbcType=VARCHAR}


	AND hydal.create_time >= unix_timestamp(#{startDate,jdbcType=VARCHAR})


	AND hydal.create_time <= unix_timestamp(#{endDate,jdbcType=VARCHAR})

mapper.select:
SELECT hydal.id, #{trade,jdbcType=VARCHAR} AS roleId, FROM_UNIXTIME( hydal.create_time, '%Y-%m-%d %H:%i:%s' ) AS time, CASE WHEN hydal.`type` = 1 THEN '+' WHEN hydal.`type` = 2 THEN '-' ELSE '' END AS type, hydat.`name` AS type_name, hydat.`value` AS trade, IFNULL(hydal.amount,0) AS money, CASE WHEN hydal.is_bank = 1 THEN IFNULL(hydal.bank_balance, 0) ELSE IFNULL(hydal.balance, 0) END AS balance, IFNULL(hydal.remark, '') AS borrowNid, hydal.is_bank AS is_bank, hydat.type_id AS `revuAndExpType`, hydal.trade_status AS trade_status, hydal.remark FROM huiyingdai_account_list hydal LEFT JOIN huiyingdai_account_trade hydat ON hydal.trade = hydat.`value` AND hydat.`status` = 1
ORDER BY hydal.create_time DESC,hydal.id DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT hydar.id, FROM_UNIXTIME( hydar.create_time, '%Y-%m-%d %H:%i:%s' ) AS time, hydar.money AS money, hydar.balance AS balance, hydar.fee AS fee, hydar.is_bank AS is_bank, hydar.`status` AS `status` FROM huiyingdai_account_recharge hydar
ORDER BY hydar.create_time DESC,hydar.id DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT hydaw.id, FROM_UNIXTIME(hydaw.create_time,'%Y-%m-%d %H:%i:%s') AS time, hydaw.total AS money, hydaw.fee AS fee, hydaw.credited AS balance, hydaw.`status` AS `status`, hydaw.bank_flag AS bank_flag FROM ht_account_withdraw hydaw
ORDER BY hydaw.create_time DESC,hydaw.id DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
