SELECT hydu.user_id, hydu.username AS user_name, hydurefer.username AS recommend_name, hydd.`name` AS depart_name, hydup.source_name AS regist_pid_name, hydu.reg_ip AS regist_ip,
DATE_FORMAT(hydu.reg_time,'%Y-%m-%d %H:%i:%S') AS regist_time,
DATE_FORMAT(hydac.create_time,'%Y-%m-%d %H:%i:%S') AS open_account_time, hyduc.rl_name AS em_name, hyduc.rl_phone AS em_phone, hydu.email, hydui.email_isApprove AS email_status, hydu.mobile, hydui.mobile_isApprove AS mobile_status, hydui.truename AS real_name, hydui.truename_isApprove AS real_name_status, hydu.`status` AS account_status, hydac.chinapnr_usrid AS account, hydac.chinapnr_usrcustid AS customer_account, hydui.idcard AS id_card, hydui.borrower_type, hydu.is_set_password AS is_set_password, hydu.reg_esb AS regist_plat, hydu.account_esb AS open_account_plat, hydui.role_id AS role, hydui.attribute AS user_property, hyduc.relation AS em_relation, hydu.user_type AS user_type FROM ht_user hydu LEFT JOIN ht_user_info hydui ON hydu.user_id = hydui.user_id LEFT JOIN ht_user_contact hyduc ON hydu.user_id = hyduc.user_id LEFT JOIN ht_account_chinapnr hydac ON hydac.user_id = hydu.user_id LEFT JOIN ht_utm_reg hydur ON hydur.user_id = hydu.user_id LEFT JOIN ht_utm hydutm ON hydur.utm_id = hydutm.utm_id LEFT JOIN ht_utm_plat hydup ON hydup.source_id = hydutm.source_id LEFT JOIN ht_spreads_user hydsu ON hydsu.user_id = hydu.user_id LEFT JOIN ht_user hydurefer ON hydurefer.user_id = hydsu.spreads_user_id LEFT JOIN ht_r_oa_users oau ON oau.hyd_id = hydsu.spreads_user_id and oau.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN ht_r_oa_department hydd ON hydd.id = oau.departmentid WHERE hydu.user_id =#{userId,jdbcType=INTEGER}
