mapper.sql:
SELECT a.utm_source AS source_name, a.utm_term AS key_word, ( SELECT COUNT(visit.id) FROM ht_utm_visit visit WHERE visit.utm_id = a.utm_id ) AS access_number, ( SELECT COUNT(reg.id) FROM ht_utm_reg reg WHERE reg.utm_id = a.utm_id ) AS regist_number, ( SELECT COUNT(users.user_id) FROM ht_utm_reg reg INNER JOIN ht_user users ON users.user_id = reg.user_id AND users.open_account = 1 WHERE reg.utm_id = a.utm_id ) AS account_number FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.utm_term FROM ht_utm utm
AND utm.utm_source = #{sourceIdSrch}
AND utm.utm_term LIKE CONCAT( #{keyWordSrch}, '%')
) a

