SELECT CASE WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 60 THEN '60岁以上' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 50 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 59 THEN '50-59岁' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 40 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 49 THEN '40-49岁' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 30 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 39 THEN '30-39岁' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 18 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 29 THEN '18-29岁' END AS title, count(*) as dealSum FROM ht_user u LEFT JOIN ht_user_info ui ON u.user_id = ui.user_id WHERE u.user_id in
#{item.userId}
GROUP BY CASE WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 60 THEN '60岁以上' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 50 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 59 THEN '50-59岁' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 40 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 49 THEN '40-49岁' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 30 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 39 THEN '30-39岁' WHEN TIMESTAMPDIFF(YEAR, ui.birthday, now()) >= 18 AND TIMESTAMPDIFF(YEAR, ui.birthday, now()) <= 29 THEN '18-29岁' END;

