mapper.select:
SELECT
mobile
FROM
ht_user


        AND create_time >= #{re_time_begin,jdbcType=INTEGER}


        AND create_time <= #{re_time_end,jdbcType=INTEGER}


        AND bank_open_account = #{open_account,jdbcType=INTEGER}

SELECT hu.mobile FROM ht_user hu INNER JOIN ht_user_info hui ON hu.user_id = hui.user_id
bank_open_account = 1
AND hui.birthday LIKE #{birthday}
