mapper.select:
SELECT t.*, `od3`.`name` AS `regionName`, `od2`.`name` AS `branchName`, `od`.`name` AS `departmentName` FROM ( SELECT hydu.user_id, hydu.username AS user_name, hydui.truename AS real_name, hydu.mobile, hydu.user_type AS type_id, hydui.role_id, hydui.attribute, hydui.birthday, hydui.sex, hydui.idcard, concat(province, city, area) AS regist_area, hydu.open_account, hydu.`status`, hydu.reg_esb, hydu.reg_time AS regTime, hydui.role_id AS user_role, hydui.attribute AS user_property, hydu.open_account AS account_status, hydu.`status` AS user_status, hydu.reg_esb AS regist_plat, hydu.user_type AS AS user_type, hydur.username AS recommend_name, FROM_UNIXTIME( hydu.reg_time, '%Y-%m-%d %H:%i:%s' ) AS reg_time, CASE WHEN hydui.attribute = 2 OR hydui.attribute = 3 THEN hydu.user_id ELSE hydsu.spreads_user_id END AS hyd_id, hydui.borrower_type, hydu.bank_open_account bank_open_account, hydu.inst_code AS instCode, hca.customer_id, hboa.account, hboa.create_time AS bank_open_time FROM
(SELECT * FROM ht_user ORDER BY reg_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
) hydu
ht_user hydu
LEFT JOIN ht_user_info hydui ON hydu.user_id = hydui.user_id LEFT JOIN ht_spreads_user hydsu ON hydsu.user_id = hydu.user_id LEFT JOIN ht_user hydur ON hydur.user_id = hydsu.spreads_user_id LEFT JOIN ht_bank_open_account hboa ON hydu.user_id = hboa.user_id LEFT JOIN ht_certificate_authority hca ON hydu.user_id = hca.user_id WHERE 1=1
AND hydu.username = #{userName}
AND hydu.mobile = #{mobile}
AND hydu.user_type = #{userType,jdbcType=INTEGER}
) t LEFT JOIN ht_r_oa_users `ou` ON `ou`.hyd_id = t.hyd_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_r_oa_department `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN ht_r_oa_department `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN ht_r_oa_department `od3` ON `od3`.`id` = `od2`.`parentid`
ORDER BY t.reg_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

choose.otherwise:
SELECT COUNT(*) FROM( SELECT hydu.user_id, hydu.username AS user_name, hydui.truename AS real_name, hydu.mobile, hydu.user_type AS type_id, hydui.role_id, hydui.attribute, hydui.birthday, hydui.sex, hydui.idcard, concat(province, city, area) AS regist_area, hydu.open_account, hydu.`status`, hydu.reg_esb, hydu.reg_time AS regTime, hydui.role_id AS user_role, hydui.attribute AS user_property, hydu.open_account AS account_status, hydu.`status` AS user_status, hydu.reg_esb AS regist_plat, hydu.user_type AS AS user_type, hydur.username AS recommend_name, FROM_UNIXTIME( hydu.reg_time, '%Y-%m-%d %H:%i:%s' ) AS reg_time, CASE WHEN hydui.attribute = 2 OR hydui.attribute = 3 THEN hydu.user_id ELSE hydsu.spreads_user_id END AS hyd_id, hydui.borrower_type, hydu.bank_open_account bank_open_account, hydu.inst_code AS instCode, hca.customer_id, hboa.account, hboa.create_time AS bank_open_time FROM ht_user hydu LEFT JOIN ht_user_info hydui ON hydu.user_id = hydui.user_id LEFT JOIN ht_spreads_user hydsu ON hydsu.user_id = hydu.user_id LEFT JOIN ht_user hydur ON hydur.user_id = hydsu.spreads_user_id LEFT JOIN ht_bank_open_account hboa ON hydu.user_id = hboa.user_id LEFT JOIN ht_certificate_authority hca ON hydu.user_id = hca.user_id WHERE 1=1
AND hydu.username = #{userName}
AND hydu.mobile = #{mobile}
AND hydu.user_type = #{userType,jdbcType=INTEGER}
) t LEFT JOIN ht_r_oa_users `ou` ON `ou`.hyd_id = t.hyd_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2','Q21') LEFT JOIN ht_r_oa_department `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN ht_r_oa_department `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN ht_r_oa_department `od3` ON `od3`.`id` = `od2`.`parentid`
