sql.where:
    t.vip_id IS NOT NULL

		AND t.user_name LIKE CONCAT('%', #{userName}, '%')


		AND t.real_name LIKE CONCAT('%', #{realName}, '%')


		AND t.recommend_name LIKE CONCAT('%', #{recommendName}, '%')


		AND t.mobile LIKE CONCAT('%', #{mobile}, '%')


		AND t.role_id = #{userRole,jdbcType=INTEGER}


		AND t.attribute = #{userProperty,jdbcType=INTEGER}


		AND t.open_account = #{accountStatus,jdbcType=INTEGER}


		AND t.`status` = #{userStatus,jdbcType=INTEGER}


		AND t.reg_esb = #{registPlat,jdbcType=INTEGER}


		AND t.is51_cd = #{is51,jdbcType=INTEGER}


		AND FROM_UNIXTIME( t.regTime, '%Y-%m-%d' ) >= #{regTimeStart}


		AND FROM_UNIXTIME( t.regTime, '%Y-%m-%d' ) <= #{regTimeEnd}


		AND od.id in

			#{dep}

mapper.select:
SELECT t.*, `od3`.`name` AS `regionName`, `od2`.`name` AS `branchName`, `od`.`name` AS `departmentName` FROM ( SELECT hydu.user_id, hydu.username AS user_name, hydui.truename AS real_name, hydu.mobile, hydui.vip_id, IFNULL(hvi.vip_name,"") AS vip_name, IFNULL(hydui.vip_value,"0") AS vip_value, CASE hydui.vip_platform WHEN 0 THEN 'PC' WHEN 1 THEN '微官网 ' WHEN 2 THEN 'Android' WHEN 3 THEN 'iOS' WHEN 4 THEN '其他' ELSE IFNULL(hydui.vip_platform,'') END vip_platform, hydui.role_id, hydui.attribute, hydu.open_account, hydu.`status`, hydu.reg_esb, hydui.is51 as is51_cd, hydu.reg_time as regTime, pnr.`name` AS user_role, pna.`name` AS user_property, pnac.`name` AS account_status, pnu.`name` AS user_status, hydur.username AS recommend_name, pnrp.`name` AS regist_plat, pnis51.`name` AS is51, FROM_UNIXTIME( hydu.reg_time, '%Y-%m-%d %H:%i:%s' ) AS reg_time, CASE WHEN hydui.attribute = 2 OR hydui.attribute = 3 THEN hydu.user_id ELSE hydsu.spreads_userid END AS hyd_id, FROM_UNIXTIME( hvuu.add_time, '%Y-%m-%d %H:%i:%s' ) AS vip_add_time FROM ht_users hydu LEFT JOIN ht_users_info hydui ON hydu.user_id = hydui.user_id LEFT JOIN ht_vip_info hvi ON hvi.id = hydui.vip_id LEFT JOIN ht_spreads_users hydsu ON hydsu.user_id = hydu.user_id LEFT JOIN ht_users hydur ON hydur.user_id = hydsu.spreads_userid -- LEFT JOIN hyjf_param_name pnr ON cast(hydui.role_id AS CHAR) = pnr.name_cd AND pnr.name_class = 'USER_ROLE' -- LEFT JOIN hyjf_param_name pna ON cast(hydui.attribute AS CHAR) = pna.name_cd AND pna.name_class = 'USER_PROPERTY' -- LEFT JOIN hyjf_param_name pnac ON cast(hydu.open_account AS CHAR) = pnac.name_cd AND pnac.name_class = 'ACCOUNT_STATUS' -- LEFT JOIN hyjf_param_name pnu ON cast(hydu.`status` AS CHAR) = pnu.name_cd AND pnu.name_class = 'USER_STATUS' -- LEFT JOIN hyjf_param_name pnrp ON cast(hydu.reg_esb AS CHAR) = pnrp.name_cd AND pnrp.name_class = 'CLIENT' -- LEFT JOIN hyjf_param_name pnis51 ON cast(hydui.is51 AS CHAR) = pnis51.name_cd AND pnis51.name_class = 'IS_51' LEFT JOIN ht_vip_user_upgrade hvuu ON hvuu.user_id=hydu.user_id AND hvuu.upgrade_vip_type=1 ) t LEFT JOIN ht_r_oa_users `ou` ON `ou`.hyd_id = t.hyd_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2','Q21') LEFT JOIN ht_r_oa_department `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN ht_r_oa_department `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN ht_r_oa_department `od3` ON `od3`.`id` = `od2`.`parentid`
ORDER BY t.reg_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
