mapper.select:
SELECT hydu.user_id, hydu.username, hvi.vip_name, CASE WHEN hvu.upgrade_vip_type = 1 THEN '购买' WHEN hvu.upgrade_vip_type = 2 THEN 'V值升级' ELSE '' END upgrade_vip_type, FROM_UNIXTIME(hvu.add_time, '%Y-%m-%d %H:%i:%s' ) upgrade_time, IFNULL(hvu.upgrade_vip_value,0) upgrade_vip_value, CASE WHEN hvu.gift_flg = 0 THEN '未发放' WHEN hvu.gift_flg = 1 THEN '已发放' ELSE '' END gift_flg, hvu.remark FROM ht_vip_user_upgrade hvu LEFT JOIN ht_vip_info hvi ON hvu.vip_id = hvi.id LEFT JOIN ht_users hydu ON hvu.user_id = hydu.user_id
ORDER BY hvi.vip_level ASC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
