mapper.select:
SELECT br.nid AS orderNo, 2 AS orderStatus, br.user_id AS assetCustomerId, 1 AS assetId, "" AS empNo, 2 AS productType, br.borrow_nid AS productNo, '散标' AS productName, br.recover_capital AS transAmount, FROM_UNIXTIME(bt.create_time,'%Y-%m-%d %H:%i:%s') AS transTime, b.borrow_period AS deadlineNum, CASE WHEN b.is_month=0 THEN 1 WHEN b.is_month=1 THEN 2 END AS DeadlineUnit, ta.contract_number AS contractNumber FROM ht_borrow_recover br LEFT JOIN ht_borrow b ON br.borrow_nid =b.borrow_nid left join ht_borrow_tender bt ON br.nid = bt.nid LEFT JOIN ht_tender_agreement ta ON ta.tender_nid = br.nid LEFT JOIN hyjf_user.ht_utm_reg ur ON br.user_id = ur.user_id WHERE br.status=1 and br.accede_order_id is null
AND ur.utm_id in
#{item}
AND b.recover_last_time >= STR_TO_DATE(#{startTime},'%Y/%m/%d %H:%i:%s')
AND b.recover_last_time <= STR_TO_DATE(#{endTime},'%Y/%m/%d %H:%i:%s')
AND br.user_id = #{assetCustomerId}
union all SELECT ha.accede_order_id AS orderNo, 2 AS orderStatus, ha.user_id AS assetCustomerId, 1 AS assetId, "" AS empNo, 3 AS productType, ha.plan_nid AS productNo, '智投' AS productName, ha.accede_account AS transAmount, date_format(ha.create_time,'%Y-%m-%d %H:%i:%s') AS transTime, hp.lock_period AS deadlineNum, CASE WHEN hp.is_month=0 THEN 1 WHEN hp.is_month=1 THEN 2 END AS DeadlineUnit, ta.contract_number AS contractNumber FROM ht_hjh_accede ha LEFT JOIN ht_hjh_plan hp ON ha.plan_nid=hp.plan_nid LEFT JOIN ht_tender_agreement ta ON ta.tender_nid = ha.accede_order_id LEFT JOIN hyjf_user.ht_utm_reg ur ON ha.user_id = ur.user_id WHERE ha.order_status in (0,2,3)
AND ur.utm_id in
#{item}
AND ha.create_time >= STR_TO_DATE(#{startTime},'%Y/%m/%d %H:%i:%s')
AND ha.create_time <= STR_TO_DATE(#{endTime},'%Y/%m/%d %H:%i:%s')
AND ha.user_id = #{assetCustomerId}
