mapper.select:
select * from (SELECT br.user_id AS assetCustomerId, CONCAT(br.nid,br.recover_period) AS refundOrderNumber, br.nid AS orderNo, br.borrow_nid AS productNo, #{entId} AS entId, 1 AS assetId, FROM_UNIXTIME(br.recover_time,'%Y-%m-%d') AS expectedExitTime, '' AS actualExitTime, br.recover_interest-br.credit_interest_amount AS expectedRepay, '' AS actualRepay, br.recover_capital - br.credit_amount AS expectedExitCapital, '' AS actualExitCapital, br.recover_interest-br.credit_interest_amount+br.recover_capital - br.credit_amount AS expectedExitAmount, '' AS actualExitAmount, br.recover_period AS period, 1 AS status FROM ht_borrow_recover br LEFT JOIN ht_borrow b ON br.borrow_nid = b.borrow_nid LEFT JOIN hyjf_user.ht_utm_reg ur ON br.user_id = ur.user_id WHERE br.recover_status=0 and br.accede_order_id is null AND b.borrow_style IN ( 'endday', 'end' ) AND br.credit_status <> 2 AND b.recover_last_time >= unix_timestamp( '2018-06-01' )
AND br.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
UNION ALL SELECT brp.user_id AS assetCustomerId, CONCAT(brp.nid,brp.recover_period) AS refundOrderNumber, brp.nid AS orderNo, brp.borrow_nid AS productNo, #{entId} AS entId, 1 AS assetId, FROM_UNIXTIME( brp.recover_time, '%Y-%m-%d' ) AS expectedExitTime, '' AS actualExitTime, brp.recover_interest-brp.credit_interest AS expectedRepay, '' AS actualRepay, brp.recover_capital - brp.credit_amount AS expectedExitCapital, '' AS actualExitCapital, brp.recover_interest-brp.credit_interest+ brp.recover_capital - brp.credit_amount AS expectedExitAmount, '' AS actualExitAmount, brp.recover_period AS period, 1 AS STATUS FROM ht_borrow_recover_plan brp LEFT JOIN ht_borrow b ON brp.borrow_nid = b.borrow_nid LEFT JOIN hyjf_user.ht_utm_reg ur ON brp.user_id = ur.user_id WHERE brp.recover_status=0 and brp.accede_order_id is null AND brp.credit_status <> 2 AND b.recover_last_time >= unix_timestamp( '2018-06-01' )
AND br.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
union all SELECT cr.user_id AS assetCustomerId, CONCAT(cr.assign_nid,cr.assign_repay_period) AS refundOrderNumber, cr.assign_nid AS orderNo, cr.bid_nid AS productNo, #{entId} AS entId, 1 AS assetId, FROM_UNIXTIME( cr.assign_repay_next_time, '%Y-%m-%d' ) AS expectedExitTime, '' AS actualExitTime, cr.assign_interest AS expectedRepay, '' AS actualRepay, cr.assign_capital AS expectedExitCapital, '' AS actualExitCapital, cr.assign_account AS expectedExitAmount, '' AS actualExitAmount, cr.assign_repay_period AS period, 1 AS STATUS FROM ht_credit_repay cr LEFT JOIN ht_borrow b ON cr.bid_nid = b.borrow_nid LEFT JOIN hyjf_user.ht_utm_reg ur ON cr.user_id = ur.user_id WHERE cr.`status` = 0 AND b.recover_last_time >= unix_timestamp( '2018-06-01' )
AND cr.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
union all SELECT ha.user_id AS assetCustomerId, CONCAT(ha.accede_order_id,'1') AS refundOrderNumber, ha.accede_order_id AS orderNo, ha.plan_nid AS productNo, #{entId} AS entId, 1 AS assetId, FROM_UNIXTIME( ha.quit_time, '%Y-%m-%d' ) AS expectedExitTime, '' AS actualExitTime, ha.should_pay_interest AS expectedRepay, ha.received_interest AS actualRepay, ha.should_pay_capital AS expectedExitCapital, ha.received_capital AS actualExitCapital, ha.should_pay_total AS expectedExitAmount, ha.received_total AS actualExitAmount, 1 AS period, 1 AS STATUS FROM ht_hjh_repay hr LEFT JOIN ht_hjh_accede ha ON ha.accede_order_id = hr.accede_order_id LEFT JOIN hyjf_user.ht_utm_reg ur ON hr.user_id = ur.user_id WHERE hr.repay_status IN ( 0, 1 ) AND ha.acctual_payment_time >= unix_timestamp( '2018-06-01' )
AND hr.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
) as recover
LIMIT #{limitStart} , #{limitEnd}

select count(*) from (SELECT br.user_id AS assetCustomerId FROM ht_borrow_recover br LEFT JOIN ht_borrow b ON br.borrow_nid = b.borrow_nid LEFT JOIN hyjf_user.ht_utm_reg ur ON br.user_id = ur.user_id WHERE br.recover_status=0 and br.accede_order_id is null AND b.borrow_style IN ( 'endday', 'end' ) AND br.credit_status <> 2 AND b.recover_last_time >= unix_timestamp( '2018-06-01' )
AND br.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
UNION ALL SELECT brp.user_id AS assetCustomerId FROM ht_borrow_recover_plan brp LEFT JOIN ht_borrow b ON brp.borrow_nid = b.borrow_nid LEFT JOIN hyjf_user.ht_utm_reg ur ON brp.user_id = ur.user_id WHERE brp.recover_status=0 and brp.accede_order_id is null AND brp.credit_status <> 2 AND b.recover_last_time >= unix_timestamp( '2018-06-01' )
AND br.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
union all SELECT cr.user_id AS assetCustomerId FROM ht_credit_repay cr LEFT JOIN ht_borrow b ON cr.bid_nid = b.borrow_nid LEFT JOIN hyjf_user.ht_utm_reg ur ON cr.user_id = ur.user_id WHERE cr.`status` = 0 AND b.recover_last_time >= unix_timestamp( '2018-06-01' )
AND cr.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
union all SELECT ha.user_id AS assetCustomerId FROM ht_hjh_repay hr LEFT JOIN ht_hjh_accede ha ON ha.accede_order_id = hr.accede_order_id LEFT JOIN hyjf_user.ht_utm_reg ur ON hr.user_id = ur.user_id WHERE hr.repay_status IN ( 0, 1 ) AND ha.acctual_payment_time >= unix_timestamp( '2018-06-01' )
AND hr.user_id = #{assetCustomerId}
AND ur.utm_id in
#{item}
) countR
