mapper.select:
SELECT hydu.user_id, hydu.username, hydu.mobile, hydui.truename AS real_name, hydui.idcard AS id_card, hydu.open_account AS account_status_name, hbc.card_no AS account, hboa.account AS customer_account, hydu.bank_account_esb AS open_account_plat, date_format(hboa.create_time,'%Y-%m-%d %H:%i:%s') AS open_time FROM ht_user hydu LEFT JOIN ht_user_info hydui ON hydu.user_id = hydui.user_id INNER JOIN ht_bank_open_account hboa ON hboa.user_id = hydu.user_id LEFT JOIN ht_bank_card hbc ON hbc.user_id = hydu.user_id
ORDER BY hboa.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

sql.where:
hydu.bank_open_account = 1
AND hydu.user_id = #{userId,jdbcType=INTEGER}
AND hydu.username LIKE CONCAT(#{userName,jdbcType=VARCHAR}, '%')
AND hydu.mobile LIKE CONCAT(#{mobile,jdbcType=VARCHAR}, '%')
AND hboa.account = #{customerAccount,jdbcType=VARCHAR}
AND hydui.idcard LIKE CONCAT(#{idCard,jdbcType=VARCHAR}, '%')
AND hydui.truename LIKE CONCAT(#{realName,jdbcType=VARCHAR}, '%')
AND hydu.bank_account_esb = #{openAccountPlat,jdbcType=VARCHAR}
AND hboa.create_time >= concat(#{openTimeStart,jdbcType=VARCHAR},' 00:00:00')
AND hboa.create_time <= concat(#{openTimeEnd,jdbcType=VARCHAR},' 23:59:59')
